import i18n from 'i18next';
import { initReactI18next } from 'react-i18next';
import LanguageDetector from 'i18next-browser-languagedetector';

import enTranslation from './locales/en.json';
import esTranslation from './locales/es.json';

// Configuración de i18next
i18n
  // Detecta el idioma del navegador
  .use(LanguageDetector)
  // Pasa el módulo i18n a react-i18next
  .use(initReactI18next)
  // Inicializa i18next
  .init({
    resources: {
      en: {
        translation: enTranslation
      },
      es: {
        translation: esTranslation
      }
    },
    fallbackLng: 'es', // Idioma por defecto si no se detecta ninguno
    debug: false, // Desactiva los logs de depuración en producción

    // Opciones comunes de detección, no todas son necesarias
    detection: {
      order: ['querystring', 'cookie', 'localStorage', 'navigator', 'htmlTag'],
      cache: ['localStorage', 'cookie']
    },

    interpolation: {
      escapeValue: false // No escapamos valores con React ya que ya lo hace
    },
  });

export default i18n;
