import React, { useState, useContext, useEffect } from 'react';
import { Card, Button, Container, Row, Col, Alert } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import { ethers } from 'ethers';
import AuthContext from '../../context/AuthContext';
import { toast } from 'react-toastify';

const Login = () => {
  const { isAuthenticated, login } = useContext(AuthContext);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    // If already authenticated, redirect to home
    if (isAuthenticated) {
      navigate('/');
    }
  }, [isAuthenticated, navigate]);

  const connectWallet = async () => {
    setIsLoading(true);
    setError('');

    try {
      // Check if MetaMask is installed
      if (!window.ethereum) {
        throw new Error('MetaMask is not installed. Please install MetaMask to use this application.');
      }

      // Request account access
      const accounts = await window.ethereum.request({ method: 'eth_requestAccounts' });

      if (accounts.length === 0) {
        throw new Error('No Ethereum accounts found. Please make sure you have an account in MetaMask.');
      }

      const address = accounts[0];

      // Get nonce from server
      const nonceResponse = await fetch(`${process.env.REACT_APP_API_URL || 'http://localhost:5000'}/api/auth/nonce`);
      const nonceData = await nonceResponse.json();

      if (!nonceData.success) {
        throw new Error('Failed to get authentication nonce from server.');
      }

      // Sign the message with the nonce
      const provider = new ethers.providers.Web3Provider(window.ethereum);
      const signer = provider.getSigner();
      const signature = await signer.signMessage(nonceData.message);

      // Verify signature with the server
      const authResponse = await fetch(`${process.env.REACT_APP_API_URL || 'http://localhost:5000'}/api/auth/verify-signature`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          address,
          signature,
          message: nonceData.message
        })
      });

      const authData = await authResponse.json();

      if (!authData.success) {
        throw new Error('Authentication failed. Please try again.');
      }

      // Login successful
      login(address, authData.token);
      toast.success('Successfully connected wallet!');
      navigate('/');
    } catch (error) {
      console.error('Login error:', error);
      setError(error.message || 'Failed to connect wallet. Please try again.');
      toast.error(error.message || 'Failed to connect wallet');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <Container className="mt-5">
      <Row className="justify-content-center">
        <Col md={8} lg={6}>
          <Card className="shadow-sm">
            <Card.Body className="p-5">
              <div className="text-center mb-4">
                <i className="fas fa-wallet fa-3x text-primary mb-3"></i>
                <h2>Connect Your Wallet</h2>
                <p className="text-muted">
                  Connect your blockchain wallet to authenticate and participate in elections.
                </p>
              </div>

              {error && <Alert variant="danger">{error}</Alert>}

              <Button 
                variant="primary" 
                size="lg" 
                block 
                className="mb-4 w-100 d-flex align-items-center justify-content-center"
                onClick={connectWallet}
                disabled={isLoading}
              >
                {isLoading ? (
                  <>
                    <span className="spinner-border spinner-border-sm me-2" role="status" aria-hidden="true"></span>
                    Connecting...
                  </>
                ) : (
                  <>
                    <img 
                      src="https://metamask.io/images/metamask-fox.svg" 
                      alt="MetaMask" 
                      style={{ height: '24px', marginRight: '10px' }} 
                    />
                    Connect with MetaMask
                  </>
                )}
              </Button>

              <div className="text-center mt-4">
                <h5>Why Connect a Wallet?</h5>
                <p className="small">
                  Your wallet provides a secure way to authenticate and sign transactions on the blockchain.
                  This ensures the integrity of your vote and prevents fraud.
                </p>
                <hr />
                <p className="mb-0 text-muted small">
                  Don't have a wallet? <a href="https://metamask.io/" target="_blank" rel="noreferrer">Install MetaMask</a>
                </p>
              </div>
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
};

export default Login;
