import { signMessage, setupWeb3Provider, getConnectedAddress } from '../utils/web3Utils';

/**
 * Servicio de autenticaciu00f3n para manejar la conexiu00f3n con MetaMask y firma de mensajes
 */
export const authService = {
  /**
   * Conecta con MetaMask y autentica al usuario
   * @param {string} userName - Nombre del usuario (opcional)
