    try {
      // Configurar proveedor Web3
      const web3Setup = await setupWeb3Provider();
      if (!web3Setup) {
        return { success: false, error: 'No se pudo conectar a MetaMask' };
      }

      const { provider, signer } = web3Setup;
      const address = await signer.getAddress();

      // Obtener nonce del servidor
      const nonceResponse = await fetch(`${process.env.REACT_APP_API_URL || 'http://localhost:5000'}/api/auth/nonce`, {
        headers: {
          'Content-Type': 'application/json'
        }
      });

      const nonceData = await nonceResponse.json();
      if (!nonceData.success) {
        return { success: false, error: nonceData.message || 'Error al obtener nonce de autenticaciu00f3n' };
      }

      // Firmar mensaje con nonce
      const signature = await signer.signMessage(nonceData.message);
      if (!signature) {
        return { success: false, error: 'Error al firmar el mensaje' };
      }

      // Verificar firma en el servidor
      const authResponse = await fetch(`${process.env.REACT_APP_API_URL || 'http://localhost:5000'}/api/auth/verify-signature`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({
          address,
          signature,
          message: nonceData.message,
          name: userName
        })
      });

      const authData = await authResponse.json();
      if (!authData.success) {
        return { success: false, error: authData.message || 'Error de autenticaciu00f3n' };
      }

      // Autenticaciu00f3n exitosa
      return {
        success: true,
        address,
        token: authData.token,
        name: authData.name || 'Usuario'
      };
    } catch (error) {
      console.error('Error en autenticaciu00f3n:', error);
      return {
        success: false,
        error: error.message || 'Error desconocido durante la autenticaciu00f3n'
      };
    }
  },

  /**
   * Verifica si hay una sesiu00f3n activa guardada
   * @returns {boolean}
   */
  hasActiveSession: () => {
    const token = localStorage.getItem('auth_token');
    const address = localStorage.getItem('user_address');
    return !!(token && address);
  },

  /**
   * Obtiene el token de autenticaciu00f3n actual
   * @returns {string|null}
   */
