import React, { useState, useEffect, useContext, useCallback } from 'react';
import { Container, Row, Col, Card, Button, Table, Badge, Alert, Spinner, Tabs, Tab } from 'react-bootstrap';
import { Link, useNavigate } from 'react-router-dom';
import { useTranslation } from 'react-i18next';
import AdminContext from '../../context/AdminContext';
import { formatTimestamp, formatAddress, isElectionActive, hasElectionEnded } from '../../utils/contractUtils';
import { toast } from 'react-toastify';

const AdminDashboard = () => {
  const { t } = useTranslation();
  const [elections, setElections] = useState([]);
  const [voterStats, setVoterStats] = useState({ totalRegistered: 0, totalVoted: 0 });
  const [loading, setLoading] = useState(true);
  const [actionLoading, setActionLoading] = useState(false);
  const [error, setError] = useState('');
  const { isAdminAuthenticated, adminPermissions, adminName } = useContext(AdminContext);
  const navigate = useNavigate();

  const fetchElections = useCallback(async () => {
    try {
      setLoading(true);

      // En lugar de hacer peticiones a la API, usamos datos de ejemplo
      console.log('Cargando datos de ejemplo para el panel de administración');

      // Ejemplo de elecciones
      const mockElections = [
        {
          id: '1',
          title: 'Elección Municipal 2025',
          description: 'Elección para alcalde y concejales',
          startTime: Math.floor(Date.now() / 1000) - 86400, // Ayer
          endTime: Math.floor(Date.now() / 1000) + 86400 * 7, // Una semana desde ahora
          totalVotes: 156,
          candidateCount: 5,
          resultsFinalized: false
        },
        {
          id: '2',
          title: 'Presupuesto Participativo',
          description: 'Votación para decidir el uso de fondos municipales',
          startTime: Math.floor(Date.now() / 1000) - 86400 * 30, // Hace 30 días
          endTime: Math.floor(Date.now() / 1000) - 86400, // Ayer
          totalVotes: 243,
          candidateCount: 3,
          resultsFinalized: false
        },
        {
          id: '3',
          title: 'Consulta Ciudadana',
          description: 'Opinión sobre nuevas políticas de transporte público',
          startTime: Math.floor(Date.now() / 1000) - 86400 * 60, // Hace 60 días
          endTime: Math.floor(Date.now() / 1000) - 86400 * 30, // Hace 30 días
          totalVotes: 412,
          candidateCount: 2,
          resultsFinalized: true
        }
      ];

      setElections(mockElections);

      // Estadísticas de ejemplo
      const totalRegistered = 1500;
      const totalVoted = 811;

      setVoterStats({ totalRegistered, totalVoted });

      setError('');
      console.log('Datos de ejemplo cargados correctamente');
    } catch (error) {
      console.error('Error cargando datos de ejemplo:', error);
      setError('Error al cargar los datos de ejemplo. Por favor, intenta de nuevo.');
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    // Redirect if not authenticated as admin
    if (!isAdminAuthenticated) {
      navigate('/admin/login');
      return;
    }

    // Check if admin has permissions to view dashboard
    if (!adminPermissions.canViewDashboard) {
      toast.error('No tienes permisos para acceder al panel de administración');
      navigate('/');
      return;
    }

    fetchElections();
  }, [isAdminAuthenticated, adminPermissions, navigate, fetchElections]);

  const handleEndElection = async (electionId) => {
    try {
      setActionLoading(true);

      // Simulación de finalización de elección
      console.log('Finalizando elección con ID:', electionId);

      // Simular tiempo de procesamiento
      await new Promise(resolve => setTimeout(resolve, 1000));

      // Actualizar las elecciones localmente
      setElections(prevElections => {
        return prevElections.map(election => {
          if (election.id === electionId) {
            return {
              ...election,
              endTime: Math.floor(Date.now() / 1000) // Actualizar a la fecha actual
            };
          }
          return election;
        });
      });

      toast.success('Elección finalizada correctamente');
    } catch (error) {
      console.error('Error al finalizar la elección:', error);
      toast.error('No se pudo finalizar la elección');
    } finally {
      setActionLoading(false);
    }
  };

  const handleFinalizeResults = async (electionId) => {
    try {
      setActionLoading(true);

      // Simulación de finalización de resultados
      console.log('Finalizando resultados de la elección con ID:', electionId);

      // Simular tiempo de procesamiento
      await new Promise(resolve => setTimeout(resolve, 1000));

      // Actualizar las elecciones localmente
      setElections(prevElections => {
        return prevElections.map(election => {
          if (election.id === electionId) {
            return {
              ...election,
              resultsFinalized: true
            };
          }
          return election;
        });
      });

      toast.success('Resultados finalizados correctamente');
    } catch (error) {
      console.error('Error al finalizar los resultados:', error);
      toast.error('No se pudieron finalizar los resultados');
    } finally {
      setActionLoading(false);
    }
  };

  const getStatusBadge = (election) => {
    if (isElectionActive(election)) {
      return <Badge bg="success">{t('elections.status.active')}</Badge>;
    } else if (hasElectionEnded(election)) {
      return <Badge bg="secondary">{t('elections.status.ended')}</Badge>;
    } else {
      return <Badge bg="warning">{t('elections.status.upcoming')}</Badge>;
    }
  };

  if (loading) {
    return (
      <Container className="text-center my-5">
        <Spinner animation="border" role="status" variant="primary">
          <span className="visually-hidden">{t('common.loading')}</span>
        </Spinner>
        <p className="mt-3">{t('admin.loading_dashboard')}</p>
      </Container>
    );
  }

  if (error) {
    return (
      <Container className="my-5">
        <Alert variant="danger">{error}</Alert>
        <Button variant="primary" onClick={fetchElections}>Retry</Button>
      </Container>
    );
  }

  return (
    <Container>
      <div className="d-flex justify-content-between align-items-center mb-4">
        <div>
          <h2>Admin Dashboard</h2>
          <p className="text-muted">Welcome, {adminName}</p>
        </div>
        {adminPermissions.canCreateElections && (
          <Button as={Link} to="/admin/create-election" variant="primary">
            <i className="fas fa-plus me-2"></i>
            Create New Election
          </Button>
        )}
      </div>

      <Row className="mb-4">
        <Col md={4} className="mb-3">
          <Card className="shadow-sm h-100">
            <Card.Body className="text-center">
              <div className="display-4 text-primary mb-2">{elections.length}</div>
              <Card.Title>Total Elections</Card.Title>
            </Card.Body>
          </Card>
        </Col>
        <Col md={4} className="mb-3">
          <Card className="shadow-sm h-100">
            <Card.Body className="text-center">
              <div className="display-4 text-primary mb-2">{voterStats.totalRegistered}</div>
              <Card.Title>Registered Voters</Card.Title>
            </Card.Body>
          </Card>
        </Col>
        <Col md={4} className="mb-3">
          <Card className="shadow-sm h-100">
            <Card.Body className="text-center">
              <div className="display-4 text-primary mb-2">{voterStats.totalVoted}</div>
              <Card.Title>Total Votes Cast</Card.Title>
            </Card.Body>
          </Card>
        </Col>
      </Row>

      <Tabs defaultActiveKey="all" className="mb-4">
        <Tab eventKey="all" title="All Elections">
          <Card className="shadow-sm">
            <Card.Body>
              {elections.length === 0 ? (
                <div className="text-center py-4">
                  <p className="mb-0">No elections found. Create a new election to get started.</p>
                </div>
              ) : (
                <div className="table-responsive">
                  <Table hover className="align-middle">
                    <thead>
                      <tr>
                        <th>ID</th>
                        <th>Title</th>
                        <th>Start Time</th>
                        <th>End Time</th>
                        <th>Status</th>
                        <th>Votes</th>
                        <th>Actions</th>
                      </tr>
                    </thead>
                    <tbody>
                      {elections.map((election) => (
                        <tr key={election.id}>
                          <td>{election.id}</td>
                          <td>
                            <Link to={`/elections/${election.id}`} className="text-decoration-none">
                              {election.title}
                            </Link>
                          </td>
                          <td>{formatTimestamp(election.startTime)}</td>
                          <td>{formatTimestamp(election.endTime)}</td>
                          <td>{getStatusBadge(election)}</td>
                          <td>{election.totalVotes}/{election.candidateCount} candidates</td>
                          <td>
                            <div className="d-flex gap-2">
                              <Button 
                                as={Link} 
                                to={`/elections/${election.id}`}
                                variant="outline-primary"
                                size="sm"
                                className="me-2"
                                title="View Details"
                              >
                                <i className="fas fa-eye"></i>
                              </Button>

                              {!isElectionActive(election) && !hasElectionEnded(election) && (
                                <Button 
                                  as={Link}
                                  to={`/admin/edit-election/${election.id}`}
                                  variant="outline-secondary"
                                  size="sm"
                                  className="me-2"
                                  title="Edit Election"
                                >
                                  <i className="fas fa-edit"></i>
                                </Button>
                              )}

                              <Button 
                                as={Link}
                                to={`/admin/election/${election.id}/voters`}
                                variant="outline-primary"
                                size="sm"
                                className="me-2"
                                title="Manage Voters"
                              >
                                <i className="fas fa-users"></i>
                              </Button>

                              {isElectionActive(election) && adminPermissions.canManageElections && (
                                <Button 
                                  variant="outline-danger"
                                  size="sm"
                                  className="me-2"
                                  onClick={() => handleEndElection(election.id)}
                                  disabled={actionLoading}
                                  title="End Election"
                                >
                                  <i className="fas fa-stop-circle"></i>
                                </Button>
                              )}

                              {hasElectionEnded(election) && !election.resultsFinalized && adminPermissions.canFinalizeResults && (
                                <Button 
                                  variant="outline-success"
                                  size="sm"
                                  className="me-2"
                                  onClick={() => handleFinalizeResults(election.id)}
                                  disabled={actionLoading}
                                  title="Finalize Results"
                                >
                                  <i className="fas fa-check-circle"></i>
                                </Button>
                              )}

                              <Button 
                                as={Link}
                                to={`/admin/election/${election.id}/statistics`}
                                variant="outline-info"
                                size="sm"
                                className="me-2"
                                title="View Statistics"
                              >
                                <i className="fas fa-chart-bar"></i>
                              </Button>

                              {(hasElectionEnded(election) || election.resultsFinalized) && (
                                <Button 
                                  as={Link}
                                  to={`/elections/${election.id}/results`}
                                  variant="outline-info"
                                  size="sm"
                                  title="View Results"
                                >
                                  <i className="fas fa-chart-pie"></i>
                                </Button>
                              )}
                            </div>
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </Table>
                </div>
              )}
            </Card.Body>
          </Card>
        </Tab>
        <Tab eventKey="active" title="Active Elections">
          <Card className="shadow-sm">
            <Card.Body>
              {elections.filter(e => isElectionActive(e)).length === 0 ? (
                <div className="text-center py-4">
                  <p className="mb-0">No active elections found.</p>
                </div>
              ) : (
                <div className="table-responsive">
                  <Table hover className="align-middle">
                    <thead>
                      <tr>
                        <th>ID</th>
                        <th>Title</th>
                        <th>Start Time</th>
                        <th>End Time</th>
                        <th>Votes</th>
                        <th>Actions</th>
                      </tr>
                    </thead>
                    <tbody>
                      {elections
                        .filter(e => isElectionActive(e))
                        .map((election) => (
                          <tr key={election.id}>
                            <td>{election.id}</td>
                            <td>
                              <Link to={`/elections/${election.id}`} className="text-decoration-none">
                                {election.title}
                              </Link>
                            </td>
                            <td>{formatTimestamp(election.startTime)}</td>
                            <td>{formatTimestamp(election.endTime)}</td>
                            <td>{election.totalVotes}</td>
                            <td>
                              <div className="d-flex gap-2">
                                <Button
                                  as={Link}
                                  to={`/elections/${election.id}`}
                                  variant="outline-secondary"
                                  size="sm"
                                >
                                  <i className="fas fa-eye"></i>
                                </Button>
                                {adminPermissions.canManageElections && (
                                  <Button
                                    variant="outline-warning"
                                    size="sm"
                                    onClick={() => handleEndElection(election.id)}
                                    disabled={actionLoading}
                                  >
                                    End
                                  </Button>
                                )}
                              </div>
                            </td>
                          </tr>
                        ))}
                    </tbody>
                  </Table>
                </div>
              )}
            </Card.Body>
          </Card>
        </Tab>
        <Tab eventKey="ended" title="Ended Elections">
          <Card className="shadow-sm">
            <Card.Body>
              {elections.filter(e => hasElectionEnded(e)).length === 0 ? (
                <div className="text-center py-4">
                  <p className="mb-0">No ended elections found.</p>
                </div>
              ) : (
                <div className="table-responsive">
                  <Table hover className="align-middle">
                    <thead>
                      <tr>
                        <th>ID</th>
                        <th>Title</th>
                        <th>End Time</th>
                        <th>Total Votes</th>
                        <th>Finalized</th>
                        <th>Actions</th>
                      </tr>
                    </thead>
                    <tbody>
                      {elections
                        .filter(e => hasElectionEnded(e))
                        .map((election) => (
                          <tr key={election.id}>
                            <td>{election.id}</td>
                            <td>
                              <Link to={`/elections/${election.id}`} className="text-decoration-none">
                                {election.title}
                              </Link>
                            </td>
                            <td>{formatTimestamp(election.endTime)}</td>
                            <td>{election.totalVotes}</td>
                            <td>
                              {election.resultsFinalized ? (
                                <Badge bg="success">Yes</Badge>
                              ) : (
                                <Badge bg="warning">No</Badge>
                              )}
                            </td>
                            <td>
                              <div className="d-flex gap-2">
                                <Button
                                  as={Link}
                                  to={`/elections/${election.id}`}
                                  variant="outline-secondary"
                                  size="sm"
                                >
                                  <i className="fas fa-eye"></i>
                                </Button>
                                {!election.resultsFinalized && adminPermissions.canFinalizeResults && (
                                  <Button
                                    variant="outline-success"
                                    size="sm"
                                    onClick={() => handleFinalizeResults(election.id)}
                                    disabled={actionLoading}
                                  >
                                    Finalize
                                  </Button>
                                )}
                                {election.resultsFinalized && (
                                  <Button
                                    as={Link}
                                    to={`/elections/${election.id}/results`}
                                    variant="outline-primary"
                                    size="sm"
                                  >
                                    Results
                                  </Button>
                                )}
                              </div>
                            </td>
                          </tr>
                        ))}
                    </tbody>
                  </Table>
                </div>
              )}
            </Card.Body>
          </Card>
        </Tab>
      </Tabs>

      <Row className="mb-4">
        <Col>
          <Card className="shadow-sm">
            <Card.Header>
              <h5 className="mb-0">Recent Activity</h5>
            </Card.Header>
            <Card.Body>
              <div className="ps-2">
                <div className="activity-stream">
                  {/* In a real app, we would fetch this from an audit log */}
                  <div className="activity-item d-flex align-items-start">
                    <div className="activity-icon me-3">
                      <i className="fas fa-check-circle text-success"></i>
                    </div>
                    <div className="flex-grow-1">
                      <div className="d-flex justify-content-between">
                        <strong>Election created</strong>
                        <small className="text-muted">2 hours ago</small>
                      </div>
                      <p className="mb-0">New election "Municipal Budget 2025" was created</p>
                    </div>
                  </div>
                  <div className="activity-item d-flex align-items-start mt-3">
                    <div className="activity-icon me-3">
                      <i className="fas fa-user-plus text-primary"></i>
                    </div>
                    <div className="flex-grow-1">
                      <div className="d-flex justify-content-between">
                        <strong>Voters registered</strong>
                        <small className="text-muted">3 hours ago</small>
                      </div>
                      <p className="mb-0">5 new voters were registered for election "City Council 2025"</p>
                    </div>
                  </div>
                  <div className="activity-item d-flex align-items-start mt-3">
                    <div className="activity-icon me-3">
                      <i className="fas fa-vote-yea text-info"></i>
                    </div>
                    <div className="flex-grow-1">
                      <div className="d-flex justify-content-between">
                        <strong>Vote recorded</strong>
                        <small className="text-muted">5 hours ago</small>
                      </div>
                      <p className="mb-0">
                        New vote recorded for "School Board Election" from {formatAddress("0x1234567890abcdef1234567890abcdef12345678")}
                      </p>
                    </div>
                  </div>
                  <div className="activity-item d-flex align-items-start mt-3">
                    <div className="activity-icon me-3">
                      <i className="fas fa-flag-checkered text-warning"></i>
                    </div>
                    <div className="flex-grow-1">
                      <div className="d-flex justify-content-between">
                        <strong>Election ended</strong>
                        <small className="text-muted">1 day ago</small>
                      </div>
                      <p className="mb-0">Election "Community Garden Proposal" was marked as ended</p>
                    </div>
                  </div>
                  <div className="activity-item d-flex align-items-start mt-3">
                    <div className="activity-icon me-3">
                      <i className="fas fa-chart-pie text-success"></i>
                    </div>
                    <div className="flex-grow-1">
                      <div className="d-flex justify-content-between">
                        <strong>Results finalized</strong>
                        <small className="text-muted">2 days ago</small>
                      </div>
                      <p className="mb-0">Results for "Library Extension Vote" were finalized</p>
                    </div>
                  </div>
                </div>
              </div>
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
};

export default AdminDashboard;
