import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import { Container, Card, Row, Col, Badge, Button, Alert, Spinner } from 'react-bootstrap';
import { Chart as ChartJS, ArcElement, Tooltip, Legend, CategoryScale, LinearScale, BarElement, Title } from 'chart.js';
import { Pie, Bar } from 'react-chartjs-2';
import { formatTimestamp, canViewResults } from '../../utils/contractUtils';

// Register ChartJS components
ChartJS.register(ArcElement, Tooltip, Legend, CategoryScale, LinearScale, BarElement, Title);

const ElectionResults = () => {
  const { id } = useParams();
  const [election, setElection] = useState(null);
  const [results, setResults] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchElectionData();
  }, [id]);

  const fetchElectionData = async () => {
    try {
      setLoading(true);

      // First, fetch the election details
      const electionResponse = await fetch(`${process.env.REACT_APP_API_URL || 'http://localhost:5000'}/api/elections/${id}`);
      const electionData = await electionResponse.json();

      if (!electionData.success) {
        throw new Error(electionData.message || 'Failed to fetch election details');
      }

      setElection(electionData.election);

      // Check if results can be viewed
      if (canViewResults(electionData.election)) {
        // Then, fetch the results
        const resultsResponse = await fetch(`${process.env.REACT_APP_API_URL || 'http://localhost:5000'}/api/elections/${id}/results`);
        const resultsData = await resultsResponse.json();

        if (!resultsData.success) {
          throw new Error(resultsData.message || 'Failed to fetch election results');
        }

        setResults(resultsData);
      }

      setError('');
    } catch (error) {
      console.error('Error fetching election data:', error);
      setError(error.message || 'Failed to load election data. Please try again later.');
    } finally {
      setLoading(false);
    }
  };

  const preparePieChartData = () => {
    if (!results || !results.results) return null;

    const candidateNames = results.results.map(candidate => candidate.name);
    const voteCounts = results.results.map(candidate => parseInt(candidate.voteCount));

    // Generate colors for each segment
    const backgroundColors = results.results.map((_, index) => {
      const hue = (index * 137) % 360; // Golden angle approximation for good distribution
      return `hsla(${hue}, 70%, 60%, 0.7)`;
    });

    const borderColors = backgroundColors.map(color => color.replace('0.7', '1'));

    return {
      labels: candidateNames,
      datasets: [
        {
          data: voteCounts,
          backgroundColor: backgroundColors,
          borderColor: borderColors,
          borderWidth: 1
        }
      ]
    };
  };

  const prepareBarChartData = () => {
    if (!results || !results.results) return null;

    const candidateNames = results.results.map(candidate => candidate.name);
    const voteCounts = results.results.map(candidate => parseInt(candidate.voteCount));

    return {
      labels: candidateNames,
      datasets: [
        {
          label: 'Votes',
          data: voteCounts,
          backgroundColor: 'rgba(53, 162, 235, 0.5)',
          borderColor: 'rgba(53, 162, 235, 1)',
          borderWidth: 1
        }
      ]
    };
  };

  const pieChartOptions = {
    responsive: true,
    plugins: {
      legend: {
        position: 'right',
        labels: {
          boxWidth: 15,
          padding: 15
        }
      },
      tooltip: {
        callbacks: {
          label: function(context) {
            const value = context.raw;
            const total = context.dataset.data.reduce((a, b) => a + b, 0);
            const percentage = Math.round((value / total) * 100);
            return `${context.label}: ${value} votes (${percentage}%)`;
          }
        }
      }
    }
  };

  const barChartOptions = {
    responsive: true,
    plugins: {
      legend: {
        display: false
      },
      title: {
        display: true,
        text: 'Vote Distribution'
      },
      tooltip: {
        callbacks: {
          label: function(context) {
            const value = context.raw;
            const total = results.totalVotes;
            const percentage = Math.round((value / total) * 100);
            return `${value} votes (${percentage}%)`;
          }
        }
      }
    },
    scales: {
      y: {
        beginAtZero: true,
        title: {
          display: true,
          text: 'Number of Votes'
        }
      },
      x: {
        title: {
          display: true,
          text: 'Candidates'
        }
      }
    }
  };

  if (loading) {
    return (
      <Container className="text-center my-5">
        <Spinner animation="border" role="status" variant="primary">
          <span className="visually-hidden">Loading...</span>
        </Spinner>
        <p className="mt-3">Loading election results...</p>
      </Container>
    );
  }

  if (error) {
    return (
      <Container className="my-5">
        <Alert variant="danger">{error}</Alert>
        <Button variant="primary" onClick={fetchElectionData}>Retry</Button>
        <Button variant="outline-secondary" className="ms-2" as={Link} to={`/elections/${id}`}>
          Back to Election
        </Button>
      </Container>
    );
  }

  if (!election) {
    return (
      <Container className="my-5">
        <Alert variant="warning">Election not found.</Alert>
        <Button as={Link} to="/elections" variant="primary">
          Back to Elections
        </Button>
      </Container>
    );
  }

  if (!canViewResults(election)) {
    return (
      <Container className="my-5">
        <Alert variant="info">
          Results for this election are not available yet. Results will be published after the election ends.
        </Alert>
        <Button as={Link} to={`/elections/${id}`} variant="primary">
          Back to Election
        </Button>
      </Container>
    );
  }

  if (!results || !results.results || results.results.length === 0) {
    return (
      <Container className="my-5">
        <Alert variant="warning">No results available for this election.</Alert>
        <Button as={Link} to={`/elections/${id}`} variant="primary">
          Back to Election
        </Button>
      </Container>
    );
  }

  const pieChartData = preparePieChartData();
  const barChartData = prepareBarChartData();

  return (
    <Container>
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2>Election Results</h2>
        <Button as={Link} to={`/elections/${id}`} variant="outline-secondary">
          Back to Election
        </Button>
      </div>

      <Card className="mb-4 shadow-sm">
        <Card.Body>
          <Row>
            <Col md={8}>
              <Card.Title className="fs-3">{election.title}</Card.Title>
              <Card.Subtitle className="text-muted mb-2">ID: {election.id}</Card.Subtitle>
              <Card.Text>{election.description}</Card.Text>
            </Col>
            <Col md={4} className="text-md-end">
              <Badge bg={election.resultsFinalized ? "success" : "warning"} className="mb-2">
                {election.resultsFinalized ? "Results Finalized" : "Preliminary Results"}
              </Badge>
              <p className="mb-1"><strong>Start:</strong> {formatTimestamp(election.startTime)}</p>
              <p className="mb-1"><strong>End:</strong> {formatTimestamp(election.endTime)}</p>
              <p className="mb-1"><strong>Total Votes:</strong> {results.totalVotes}</p>
            </Col>
          </Row>
        </Card.Body>
      </Card>

      <Row>
        <Col lg={6} className="mb-4">
          <Card className="shadow-sm h-100">
            <Card.Header>
              <h5 className="mb-0">Vote Distribution</h5>
            </Card.Header>
            <Card.Body>
              <div className="chart-container">
                {pieChartData && <Pie data={pieChartData} options={pieChartOptions} />}
              </div>
            </Card.Body>
          </Card>
        </Col>

        <Col lg={6} className="mb-4">
          <Card className="shadow-sm h-100">
            <Card.Header>
              <h5 className="mb-0">Vote Comparison</h5>
            </Card.Header>
            <Card.Body>
              <div className="chart-container">
                {barChartData && <Bar data={barChartData} options={barChartOptions} />}
              </div>
            </Card.Body>
          </Card>
        </Col>
      </Row>

      <Card className="shadow-sm mb-4">
        <Card.Header>
          <h5 className="mb-0">Detailed Results</h5>
        </Card.Header>
        <Card.Body>
          <div className="table-responsive">
            <table className="table table-hover">
              <thead>
                <tr>
                  <th>Rank</th>
                  <th>Candidate</th>
                  <th>Description</th>
                  <th className="text-center">Votes</th>
                  <th className="text-center">Percentage</th>
                </tr>
              </thead>
              <tbody>
                {results.results
                  .sort((a, b) => parseInt(b.voteCount) - parseInt(a.voteCount))
                  .map((candidate, index) => {
                    const voteCount = parseInt(candidate.voteCount);
                    const percentage = (voteCount / parseInt(results.totalVotes)) * 100;

                    return (
                      <tr key={candidate.id}>
                        <td>{index + 1}</td>
                        <td><strong>{candidate.name}</strong></td>
                        <td>{candidate.description}</td>
                        <td className="text-center">{voteCount}</td>
                        <td className="text-center">{percentage.toFixed(2)}%</td>
                      </tr>
                    );
                  })}
              </tbody>
            </table>
          </div>
        </Card.Body>
      </Card>

      <Card className="shadow-sm mb-4 bg-light">
        <Card.Body>
          <h5>About Blockchain Verification</h5>
          <p>
            All vote results are stored immutably on the blockchain and can be independently verified.
            The integrity of these results is guaranteed by cryptographic proof.
          </p>
          <div className="d-flex justify-content-between align-items-center">
            <small className="text-muted">Results last updated: {new Date().toLocaleString()}</small>
            <Button variant="outline-primary" size="sm">
              <i className="fas fa-external-link-alt me-2"></i>
              Verify on Blockchain
            </Button>
          </div>
        </Card.Body>
      </Card>
    </Container>
  );
};

export default ElectionResults;
